# Menu principal du programme
def main 
    puts "----------------------------------------------------"
    tab = ["Combien y a t-il de handle ?","Quel est le handle le plus court ?","Combien y-a-t'il de hanle contenant 5 caractères ?","Combien de handle commencent par une majucule ?", "Liste des handle par ordre alphabétique", "Liste des handle trier par taille", "Position de la personne @epenser","Répartition des handle par taille (nb handle avec 1 caractre, nb handle avec 2 caractere etc.)","Sortir du programme"]
    tab.each_with_index{|val,i| puts "#{i+1}. #{val}"}
    puts "----------------------------------------------------"
    choice = 0
    while choice < 1 || choice > 9
        puts "Votre choix :"
        choice = gets.chomp.to_i
    end
    return choice
end

def number_handle
    puts "Il y as #{@journalists.length} handle dans cette array"
end

def shortest_handle
    shortest_word = ""
    @journalists.each_with_index {|val, i|
        # Initialisation : shortest_word devient le premier mot de la liste --cette ligne ne sera exécuter qu'au premier tour
        shortest_word = val if i == 0
        # Comparaison 
        shortest_word = val if val.length < shortest_word.length
    }
    puts "Comme par hasard, le handle le plus petit est : #{shortest_word}"
end

def handle_five_characters
    five_word = []
    @journalists.each {|val|
        five_word << val if val.length == 6
    }
    puts "Il y as #{five_word.length} handle qui ont 5 caractéres. Ce sont les suivants :"
    puts five_word
end

def handle_start_uppercase
    number_words = 0
    @journalists.each {|val|
        # Céer un tableau contenant chaque lettre. exemple 1er mot @jcunniet = ["@","j","c","u","n","n","i","e","t"]
        word_board = val.split(//)
        # On incremente si la seconde lettre du mot est en majucule
        number_words += 1 if word_board[1] === word_board[1].upcase
    }
    puts "Il y as #{number_words} handle qui commencent par une majucule"
end


def alphabetical_order
    alphabetical_list = []
    # On crer un tableau avec les meme valeur et index que @journalists mais sans les @ ou _ et en minuscule
    @journalists.each_with_index {|value, index|
        alphabetical_list << value.tr('@_','').downcase
    }
    #Fusion des deux tableau en un hash
    list = Hash[alphabetical_list.zip(@journalists)]

    # on trie notre liste par ordre alphabetique
    alphabetical_list = alphabetical_list.sort
    # On crer un nouveau hash avec comme valeur la liste trier par ordre alphabetique
    alphabetical_list_hash = {}
    alphabetical_list.each {|val|
        alphabetical_list_hash[val] = ""
    }
    # On incrémente les clés celon les valeur correspondante.
    list.each {|k,v|
        alphabetical_list_hash.each {|key,val| 
            alphabetical_list_hash[key] = v if k == key 
        }
    }
    #On affiche le résultat
    puts "Liste par ordre alphabétique"
    alphabetical_list_hash.each_value {|v|
        puts v
    }
end

def size_handle
    size_list = @journalists.sort {|x,y| x.length <=> y.length}
    return size_list
end

def handle_position
    position = @journalists.index("@epenser")
    puts "@epenser est à la position #{position}"
end

def repartition_handle
    size_list = size_handle
    repartition = {}
    size_list.each_with_index {|val,i|
        if repartition[val.length]
            repartition[val.length] += 1
        else 
            repartition[val.length] = 1
        end
    }
    repartition.each do |carac,count|
        puts "Il y as #{count} handle avec #{carac} caractére"
    end
end